import React from "react";
import styled from "styled-components";
import { Search as SearchIcon } from "../../../styles/icons";

const Container = styled.div`
    position: relative;
    & input {
        height: 30px; 
        padding: 6px 20px 6px 30px;
        border: 1px solid var(--i1d);
    }
`;
const Wrapper = styled.div`
    display: flex;
    align-items: center;
    height: 30px; 
    position: absolute;
    & svg {
        pointer-events: none;
    }
`;

const IconSearch = styled(SearchIcon)`
  width: 25px;
    height: 25px;
    &.adfcs{
        width: 25px;
    height: 25px;
    }
`;
const Search = () => {
    function handleFocus() {
        const fcs = document.getElementById('fcs')
        fcs.classList.add('adfcs')
    }
    return (
        <Container>
            <Wrapper>
                <IconSearch id="fcs"/>
            </Wrapper>
            <input name="" onFocus={handleFocus} placeholder="Pesquisar" id="place" type="text" />
        </Container>
    );
};

export default Search;
