import styled from "styled-components";

export const Button = styled.button`
    background-color: ${(props) => (props.outlined === "res" ? "var(--light)" : "var(--dark)")};
    border: 2px solid
        ${(props) => (props.outlined ? "var(--dark)" : "transparent")};
    color: ${(props) => (props.outlined ? "var(--dark)" : "white")};

    font-weight: bold;
    padding: 5px 10px;
    :hover{
        background-color: ${(props) => (props.outlined === "res" ? "var(--dark)" : "var(--light)")};
        color: ${(props) => (props.outlined ? "var(--dark)" : "var(--dark)")};
    }
`;
