import styled from "styled-components";

export const Button = styled.button`
    background-color: ${(props) => (props.outlined ? "red" : "black")};
    color: ${(props) => (props.outlined ? "blue" : "white")};
    border: 2px solid ${(props) => (props.outlined ? "black" : "transparent")};

    font-weight: bold;
    padding: 5px 10px;
`;
