import styled from "styled-components";

export const Button = styled.button`

    background-color: ${(props) => (props.outlined ? "var(--light)" : "black")};
    color: ${(props) => (props.outlined ? "var(--dark)" : "white")};
    border: 2px solid ${(props) => (props.outlined ? "var(--dark)" : "transparent")};

    font-weight: bold;
    padding: 5px 10px;
`;
