import React from "react";
import styled from "styled-components";
import { Search as SearchIcon } from "../../../styles/icons";

const Container = styled.div`
    position: relative;
    & input {
        height: 30px;
        padding: 6px 20px 6px 30px;
        border: 1px solid var(--i1d);
    }
`;
const Wrapper = styled.div`
    display: flex;
    height: 30px;
    width: 30px;
    position: absolute;
    & svg {
        pointer-events: none;
    }
`;

const IconSearch = styled(SearchIcon)`
    width: 25px;
    height: 25px;
    transition: 500ms ease;
    transform-box: flex;
    &.adfcs {
        width: 17px;
        height: 17px;
    }
`;
const Search = () => {
    const cls = "adfcs";
    const obj = {
        handleFocus() {
            const fcs = document.getElementById("fcs");
            fcs.classList.add(cls);
        },
        handleBlur() {
            const fcs = document.getElementById("fcs");
            fcs.classList.remove(cls);
        },
    };
    return (
        <Container>
            <Wrapper>
                <IconSearch id="fcs" />
            </Wrapper>
            <input
                name=""
                onFocus={obj.handleFocus}
                onBlur={obj.handleBlur}
                placeholder="Pesquisar"
                id="place"
                type="text"
            />
        </Container>
    );
};

export default Search;
